// src/components/Home.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

function Home() {
  const [match, setMatch] = useState({
    id: 1,
    image: 'https://via.placeholder.com/150',
    reason: 'Ihr geht beide gern wandern. Schreibstil und Interessen ähnlich. etc.',
  });

  const navigate = useNavigate();

  const handleReject = () => {
    // Logik zum Ablehnen des Vorschlags
    console.log('Match abgelehnt');
  };

  const handleAccept = () => {
    // Logik zum Akzeptieren des Vorschlags
    console.log('Match akzeptiert');
  };

  const handleChat = () => {
    // Navigation zum Chat-Fenster
    navigate(`/chat/${match.id}`);
  };
  //Chat-ID:

    //Im Beispiel wird eine statische Chat-ID verwendet (match.id). In einer echten
    //Anwendung sollte diese ID dynamisch und einzigartig sein, basierend auf dem tatsächlichen Match oder Benutzerprofil.

  return (
    <div className="home">
      <h2>Vorgeschlagener Match</h2>
      <div className="match-image">
        <img src={match.image} alt="Match" />
      </div>
      <div className="match-reason">
        <p>Warum es klappen könnte:</p>
        <p>{match.reason}</p>
      </div>
      <div className="match-buttons">
        <button className="reject" onClick={handleReject}>Don't Match</button>
        <button className="chat" onClick={handleChat}>Start Chatting</button>
        <button className="accept" onClick={handleAccept}>Match</button>
      </div>
    </div>
  );
}

export default Home;
